def choisi
puts "Que voulez-vous savoir ?"
	puts "1-Nombre de journaliste dans un array"
	puts "2-Le nombre d'handle qui contient un numero"
	puts "3-Le nombre d'handle qui contient les 4 lettres du prenom 'Aude'"
	puts "4-Le nombre commencant par une majuscule avec un @ au premiere lettre"
	puts "5-Le nombre de majuscule"
	puts "6-Le nombre de tous les pseudos avec un underscore_"
	puts "7-Liste de triage par ordre alphabetique"
	puts "8-Les 50 handles les plus courts de array"
	puts "9-La position du '@epenser' dans l'array"
puts" Repondez par les numerotations"
print">"
choix = gets.chomp.to_i

#Nombre de journaliste
		if choix == 1
	x = @liste
	puts "Il y a #{x} journalistes dans ce array"
	

#handle conntenat un numero
		elsif choix == 2
	x1 = @liste.select {|term| term =~/[0123456789]/}
	puts "#{x1.length} handle contiennent un numéro"

	
#handle contennant les 4 lettre Aude
		elsif choix == 3
	x1 = @liste.select {|term| term =~/([Aa]ude)/}
	puts "#{x1.length} handle contiennent les 4 lettres du prénom Aude"


#Nombre commencant par @ suivi d'une lettre en majuscule
		elsif choix == 4
	x1 = @liste.select {|term| term =~/(@[ABCDEFGHIJKLMNOPQRSTUVWXYZ])/}
	puts "#{x1.length} commencent par une majuscule après le @"


#nombre commencant par une lettre majuscule
		elsif choix == 5
	x1 = @liste.select {|term| term =~/[ABCDEFGHIJKLMNOPQRSTUVWXYZ]/}
	puts "#{x1.length} commencent par une majuscule"


#les pseudos comportant n underscore _
		elsif choix == 6
	x = @liste.select {|term| term =~/(_)/}
	puts "underscore dans tous les pseudos confondus"
	puts x.length


#triage alphabetique des handle
		elsif choix == 7
	puts " Triage la liste de handle par ordre alphabétique "
	puts @liste.sort

	
#50 hanles les plus courts
		elsif choix == 8
	y = @liste.sort_by(&:length)
	z=y[0..49].inspect
	puts " Les 50 handles les plus courts de ce array "
	puts z


#position d'un array
		elsif choix == 9
	puts " la position dans l'array de la personne @epenser est: "
	puts @liste.index ("@epenser")
		else
	puts "Il n'y a pas de reponse dans votre choix"
	end
end
choisi